frappe.ui.form.on("Sales Order", "refresh", function (frm, cdt, cdn) {
    frm.set_query("item_code", "items", function () {
         return{
		filters: [
			["company", "=", frm.doc.company],
			['is_sales_item', '=', '1']
		]
         }
    });
});

